import 'package:flutter/material.dart';class ColorContainer extends StatelessWidget {  Color color1,color2,color3,color4;  ColorContainer(this.color1,this.color2,this.color3,this.color4);  @override  Widget build(BuildContext context) {    return Container(      decoration: BoxDecoration(          gradient: LinearGradient(              begin: Alignment.topLeft,              end: Alignment.bottomRight,              colors: [                color1,                color2 ,                color3 ,                color4 ,              ])      ),    );  }}