import 'package:flutter/material.dart';import 'package:music/constants/constants.dart';import 'package:on_audio_query/on_audio_query.dart';class ThumbNail extends StatelessWidget {   int thumbnail;   double width;   double shadow;   double spread;   ThumbNail(this.thumbnail,this.width,this.shadow,this.spread);  @override  Widget build(BuildContext context) {    return  Container(      height:  width,      width: width,      clipBehavior: Clip.antiAlias,      decoration: BoxDecoration(          shape: BoxShape.circle,          boxShadow: [            BoxShadow(              color:black,              offset:const Offset(-1,1),              blurRadius:shadow,              spreadRadius:spread,            )          ]      ),      child: QueryArtworkWidget(        id: thumbnail,        type: ArtworkType.AUDIO,        artworkFit: BoxFit.contain,        quality: 100,        keepOldArtwork: true,      ),);  }}